'use strict';



describe('html formatter', function () {



    describe('#init', function () {



        it('should return an object describing what file extensions the formatter handles');



        it('should return an object describing what asset and content types that the formatter handles');



        describe('#compile', function () {



            it('should return the HTML file content as is');



            describe('if header options are passed', function () {



                it('should replace any <SocketStream> tags in the html with the value in the header options');



            });



        });


    });


});